
let otp = "";
function generateOtp(len){
    try{
        otp = "";//this is for i want every time reset otp merko har baar nya otp chaiye
        for(let i=0;i<len;i++){
            otp += Math.floor(Math.random()*10);
        }
     return otp;
    }catch(error){
        throw error;
    }
}

function verifyOtp(userOtp){
try{
    if(otp == userOtp){
        return true;
    }
    throw new Error("otp not matched");//error message

}catch(error){
    throw error;
}

}

module.exports = {generateOtp,verifyOtp}